(function($){
    $.fn.emptyTextBox = function(options){
		
        return this.each(function (){
			var defaultValue = $(this).val();
			$(this).blur(function (){
				if ($(this).val() == 0) $(this).val(defaultValue);
				$(this).css({color:'#CCC'});
			}).focus(function (){
				if ($(this).val() == defaultValue) $(this).val('');
				$(this).css({color:'#787878'})
			}); 
		});}
})(jQuery);
 
 

