(function($)
{
    $.fn.maxxSmoothPreloader = function(options) 
    {
        var defaults = 
        {
            fadeInSpeed: 800,
            delay:500
        };
        
        var options = $.extend(defaults, options);
        
        return this.each(function()
        {
			
			var opts = options;
				
			var obj = $(this);
			
			var items = $("img", obj);		
			
            var i = 0;
			$(items).clone();
			$(items).each(function()
			{
			var me = this;
			$(this).css({visibility:"visible",opacity:0});
			var j = i;
			setTimeout(function()
			{
			$(me).animate({visibility:"inherit",opacity:1},opts.fadeInSpeed); 
			}, i)
			i += opts.delay
			});
                
                
        });
        
    }
})(jQuery);


//Count the element at position = 3
(function($)
{
    $.fn.countThree = function(options) 
    {
		var defaults = 
        {
           className:''
        };
		var options = $.extend(defaults, options);
        return this.each(function(){
			
			var opts = options;
			
			var obj = $(this);
			
			var items = $("> *", obj);	
			
			$(items).each(function(){
			var index = $(items).index(this)+1;
			if(index%3==0 && index!=0){
				$(this).addClass(opts.className);
			}})	
        	
		})
	}
})(jQuery);




